mapper.insert:
insert into salary
values

(	
	(
	select ifNull(max(sno),0)+1 
	from salary as sno 
	)
	,#{item.username}
	,#{item.depth1}
	,#{item.depth2}
	,#{item.depth3}
	,#{item.salary}
	,#{item.grade}
	,#{item.incentive}
	,#{item.promotion}
	,#{item.posPlus}
	,#{item.smileCash}
	,#{item.ension}
	,#{item.healthIns}
	,#{item.employIns}
	,#{item.longTermIns}
	,#{item.incomeTax}
	,#{item.loincomeTax}
	,date_format(now(), '%Y-%m-%d')
)
